mapper.insert:
INSERT INTO bul_board (bul_boardnum,userid,title,contents,country,surround_place,adress,inputdate,originalFileName,savedFileName) VALUES ( BUL_BOARDNUM_SEQ.nextval, #{userid}, #{title}, #{contents}, #{country}, #{surround_place}, #{adress}, sysdate
,#{originalFileName},#{savedFileName}
,null,null
)

mapper.update:
UPDATE
	BUL_BOARD 
SET 
	title = #{title},
	contents = #{contents},
	country = #{country},
	surround_place = #{surround_place},
	adress = #{adress}

		,originalfileName = #{originalFileName}
		,savedFileName = #{savedFileName} 

	WHERE 
	bul_boardnum = #{bul_boardnum}
	and userid = #{userid}

mapper.select:
select originalFileName from BUL_BOARD 
WHERE 
bul_boardnum = #{bul_boardnum}
