mapper.insert:
INSERT INTO MAINBOARD (mainBoardNum,userid,title,country,adress,festival_intro,surround_place,inputTime,startEvent,endEvent,originalFileName,saveFileName) VALUES ( mainboard_seq.nextval, #{userid}, #{title}, #{country}, #{adress}, #{festival_intro}, #{surround_place}, sysdate, #{startEvent}, #{endEvent}
,#{originalFileName},#{saveFileName}
,null,null
)

mapper.update:
UPDATE
	MAINBOARD
SET
	title = #{title},
	country = #{country},
	adress = #{adress},
	festival_intro = #{festival_intro},
	startEvent = #{startEvent},
	endEvent = #{endEvent}

		, originalfileName = #{originalFileName}
		,saveFileName = #{saveFileName}

			WHERE 
	mainBoardNum = #{mainBoardNum}

mapper.select:
select originalFileName from MAINBOARD 
WHERE 
mainBoardNum = #{mainBoardNum}
